var liteServer = require('../');

var serv = liteServer.create({
  root: __dirname,
  port: 8080,
  tpl: './tpl'
});
serv.view({
  compress: false
})
serv.router({
  '/upload': function (req, res) {
    res.render('upload.html');
  },
  '/up': function (req, res) {
    req.getPost(function (err, data, files) {
      res.done(JSON.stringify(data));
    });
  }
});
serv.start();